import React, { useState } from "react";
import { Link } from "react-router-dom";

const MovieCard = props => {
  return (
    <div className="container pr-5 pl-5">
      <h1>{props.movie.name}</h1>//may change this because I don't know what the movie db is sending
      <img src={props.movie.image}/> //This is the image of the movie
      <p>{props.movie.description}</p>
      <div className="col-sm-6">
        <button className="btn btn-outline-secondary" type="button">Add Movie</button>//This should say rank movie if it's already added. It would be super rad to have this working, but not needed. Do this without another backend call and you're golden (state instead)
      </div>
      <div className="col-sm-6">
        <button className="btn btn-outline-secondary" type="button">Delete Movie</button>//Only render this if the movie is in the user's list of movies
      </div>
    </div>
  );
};

export default MovieCard;
