import React, { useState,useEffect } from "react";
import { Link } from "react-router-dom";
import DataService from "../services/dataService";
import ProfileCard from "./profileCard"

const Profiles = props => {

  const [profiles, setProfiles] = useState([]);
  const [searchName, setSearchName ] = useState("");
  /*
  useEffect(()=>{
      retrieveMovies();
    }, []);
    */

  const onChangeSearchName = e => {
      const searchName = e.target.value;
      setSearchName(searchName);
  };

  const retrieveUsers = (searchName) => {
      console.log(searchName);
      DataService.getUsers(searchName)
      .then(response => {
        console.log(response);
        setProfiles(response); //Gonna have to change this to match data received
      })
    };

  return (
    <div className="container pr-5 pl-5">
      <h1 className="text-center">Search for Profiles</h1>
      <div className="row pb-1">
        <div className="input-group col-lg-4">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name"
            value={searchName}
            onChange={onChangeSearchName}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={() => retrieveUsers(searchName)}
              //Need to add on click event and state event here
            >
              Search
            </button>
          </div>
        </div>
      </div>
      <div className="row pt-3 pb-3 justify-content-center">

      </div>
    </div>
  );
};
export default Profiles;
