import logo from './logo.svg';
import './App.css';
import {Route,Switch,Link} from "react-router-dom"
import {useCallback, useContext, useState, useEffect} from "react"
import Navbar from "./components/navbar";
import Footer from "./components/footer";
import Dashboard from "./components/dashboard";
import Movies from "./components/movies";
import Profiles from "./components/profiles";
import Profile from "./components/profile";
import Login from "./components/login";
import Register from "./components/register"
import { UserContext } from "./context/UserContext"

function App() {
  const [userContext, setUserContext] = useContext(UserContext);

  const verifyUser = useCallback(() => {
    fetch("http://localhost:3000/users/refreshToken", {
      method: "POST",
      credentials: "include",
      headers: {"Content-Type":"application/json"}
    })
    .then(async response => {
      if(response.ok){
        const data = await response.json();
        setUserContext(oldUserValues => {
          return {...oldUserValues, token:data.token};
        });
        console.log(userContext);
      }
      else{
        setUserContext(oldUserValues => {
          return {...oldUserValues, token:null};
        });
        console.log(userContext);
      }

      setTimeout(verifyUser, 5 * 60 * 1000);
    })
  }, [setUserContext]);


  const fetchUserDetails = useCallback(() => {
    fetch("http://localhost:3000/users/user", {
      method:"GET",
      credentials:"include",
      headers: {
        "Content-Type":"application/json",
        Authorization: `Bearer ${userContext.token}`
      }
    })
    .then(async response =>{
      if(response.ok){
        const data = await response.json();
        setUserContext(oldUserValues => {
          return {...oldUserValues, details:data};
        });
      }
      else{
        if(response.status === 401){
          window.location.reload()
        }
        else{
          setUserContext(oldUserValues => {
            return {...oldUserValues, details:null};
          });
        }
      }
    })
  }, [setUserContext, userContext.token]);

  useEffect(() => {
    verifyUser()
  }, [verifyUser]);


  return (
    <div className="outerJSXWrapper">
      <Navbar/>
      <Switch>
        <Route exact path="/" component={Dashboard}/>
        <Route exact path="/login" component={Login}/>
        <Route exact path="/register" component={Register}/>
        <Route exact path="/movies" component={Movies}/>
        <Route exact path="/profiles" component={Profiles}/>
        <Route path="/profile/:userId" component={Profile}/>
      </Switch>
      <Footer/>
    </div>
  );
}

/*
