import React, { useContext, useState,useEffect } from "react";
import { Link } from "react-router-dom";
import AuthDataService from "../services/authService";
import { useAuth0 } from "@auth0/auth0-react";
import { UserContext } from "../context/UserContext";



const Login = () => {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [userContext, setUserContext] = useContext(UserContext);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const formSubmitHandler = e => {
    e.preventDefault();
    setIsSubmitting(true);
    setError("");

    const errorMessage = "Something went wrong! Please try again later.";

    fetch("http://localhost:3000/users/login", {
      method: "POST",
      credentials: "include",
      headers:{"Content-Type":"application/json"},
      body: JSON.stringify({email: email, password: password})
    })
    .then(async response => {
      setIsSubmitting(false);
      if(!response.ok){
        if(response.status === 400){
          setError("Please fill out all fields correctly.");
        }
        else if(response.status === 401){
          setError("Invalid email and/or password combination");
        }
        else {
          setError(errorMessage);
        }
      }

      else{
        const data = await response.json();
        setUserContext(oldUserValues => {
          return {...oldUserValues, token:data.token};
        });
        console.log(userContext);
      }
    })
    .catch(e =>{
      setIsSubmitting(false);
      setError(errorMessage);
    });
  }


  return(
    <div className="container-fluid pt-5 pr-5 pl-5">
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row d-flex justify-content-center align-items-center">
        <div className="col-md-4"></div>
        <div className="col-md-4 justify-content-center">
          <form className="" onSubmit={formSubmitHandler}>
            <input
              className="form-control"
              id="email"
              placeholder="Email"
              type="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <br/>
            <input
              className="form-control"
              id="password"
              placeholder="Password"
              type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
            <br/>
            <button
            className="btn-primary"
            type="submit"
            disabled={isSubmitting}
            >{`${isSubmitting ? "Signing In" : "Sign In"}`}</button>
          </form>
        </div>
        <div className="col-md-4"></div>
      </div>
    </div>
  )

};

export default Login;
